function krumo(){}krumo.reclass=function(b,a){if(b.className.indexOf(a)<0){b.className+=(" "+a)}};krumo.unclass=function(b,a){if(b.className.indexOf(a)>-1){b.className=b.className.replace(a,"")}};krumo.toggle=function(c){var b=c.parentNode.getElementsByTagName("ul");for(var a=0;a<b.length;a++){if(b[a].parentNode.parentNode==c.parentNode){b[a].parentNode.style.display=(b[a].parentNode.style.display=="none")?"block":"none"}}if(b[0].parentNode.style.display=="block"){krumo.reclass(c,"krumo-opened")}else{krumo.unclass(c,"krumo-opened")}};krumo.over=function(a){krumo.reclass(a,"krumo-hover")};krumo.out=function(a){krumo.unclass(a,"krumo-hover")};