mapper.select:
SELECT D.V_BID_SECTION_GROUP_CODE AS GROUP_CODE, D.V_BID_SECTION_CODE AS SECTION_CODE, A.V_BIDDER_NAME AS V_BIDNAME, A.V_STATUS_0 V_STATUS, A.V_RESULT, A.N_SCORE_1, A.N_SCORE_2, D.V_BID_SECTION_NAME, D.N_CONTROL_PRICE, D.V_JSON_OBJ FROM ${PB_DB_ID}.EPB_T_PB_STATRECORD A LEFT JOIN (SELECT B.ID,B.V_JSON_OBJ,B.V_BID_SECTION_GROUP_CODE,B.V_BID_SECTION_CODE,C.V_BID_SECTION_NAME,C.V_BID_EVALUATION_STATUS,C.N_CONTROL_PRICE FROM EKB_T_TENDER_LIST B LEFT JOIN EKB_T_SECTION_INFO C ON B.V_TPID=C.V_TPID AND B.V_BID_SECTION_ID=C.V_BID_SECTION_ID) D ON A.V_BIDDER_ID=D.ID
AND (D.V_BID_EVALUATION_STATUS = '2-1' OR D.V_BID_EVALUATION_STATUS = '2-2' OR D.V_BID_EVALUATION_STATUS = '2') AND (A.V_STATUS IN ('1','0-0') OR A.V_STATUS_0 IN ('0','1'))
AND A.V_TPID=#{tpid}
AND A.V_BID_SECTION_ID = #{sid}
ORDER BY D.V_BID_SECTION_GROUP_CODE,D.V_BID_SECTION_NAME,A.V_BIDDER_NAME

SELECT D.V_BID_SECTION_GROUP_CODE AS GROUP_CODE, D.V_BID_SECTION_CODE AS SECTION_CODE, A.V_BIDDER_NAME AS V_BIDNAME, A.V_STATUS_1 V_STATUS, A.V_RESULT, A.N_SCORE_1, A.N_SCORE_2, D.V_BID_SECTION_NAME, D.N_CONTROL_PRICE FROM ${PB_DB_ID}.EPB_T_PB_STATRECORD A LEFT JOIN (SELECT B.ID,B.V_BID_SECTION_GROUP_CODE,B.V_BID_SECTION_CODE,C.V_BID_SECTION_NAME,C.V_BID_EVALUATION_STATUS,C.N_CONTROL_PRICE FROM EKB_T_TENDER_LIST B LEFT JOIN EKB_T_SECTION_INFO C ON B.V_TPID=C.V_TPID AND B.V_BID_SECTION_ID=C.V_BID_SECTION_ID) D ON A.V_BIDDER_ID=D.ID
AND (D.V_BID_EVALUATION_STATUS = '2-2' OR D.V_BID_EVALUATION_STATUS = '2') AND (A.V_STATUS IN ('1','1-0') OR A.V_STATUS_1 IN ('0','1'))
AND A.V_TPID=#{tpid}
AND A.V_BID_SECTION_ID = #{sid}
ORDER BY D.V_BID_SECTION_GROUP_CODE,D.V_BID_SECTION_NAME,A.V_BIDDER_NAME

SELECT D.V_BID_SECTION_GROUP_CODE AS GROUP_CODE, D.V_BID_SECTION_CODE AS SECTION_CODE, A.V_BIDDER_NAME AS V_BIDNAME, A.V_STATUS_1 V_STATUS, A.V_RESULT, A.N_SCORE_1, A.N_SCORE_2, A.N_SCORE_3, D.V_BID_SECTION_NAME, D.N_CONTROL_PRICE, D.N_PRICE FROM ${PB_DB_ID}.EPB_T_PB_STATRECORD A LEFT JOIN (SELECT B.ID,B.V_BID_SECTION_GROUP_CODE,B.N_PRICE,B.V_BID_SECTION_CODE,C.V_BID_SECTION_NAME,C.V_BID_EVALUATION_STATUS,C.N_CONTROL_PRICE FROM EKB_T_TENDER_LIST B LEFT JOIN EKB_T_SECTION_INFO C ON B.V_TPID=C.V_TPID AND B.V_BID_SECTION_ID=C.V_BID_SECTION_ID) D ON A.V_BIDDER_ID=D.ID
AND D.V_BID_EVALUATION_STATUS AND (A.V_STATUS IN ('1','1-0') OR A.V_STATUS_1 IN ('0','1'))
AND A.V_TPID=#{tpid}
AND A.V_BID_SECTION_ID = #{sid}
ORDER BY D.V_BID_SECTION_GROUP_CODE,D.V_BID_SECTION_NAME,A.V_BIDDER_NAME

mapper.update:

            UPDATE EKB_T_SECTION_INFO
			SET V_BID_OPEN_STATUS = #{V_BID_OPEN_STATUS}
			 WHERE V_TPID = #{TPID} AND V_BID_OPEN_STATUS NOT LIKE '10%'
